INSERT INTO `wp_posts` VALUES('440', '2', '2015-01-09 12:20:54', '2015-01-09 12:20:54', '[section bgcolor=\"#f9f9f9\" padding=\"0\"]\r\n[raw]\r\n[one_half]\r\n<div style=\"top: 50%; display: block; position: relative;\">\r\n<h3 style=\"text-align: right; top: 50%; color: #6B0031; padding-top: 160px;\">What is WINE?</h3>\r\n<p style=\"text-align: right; margin-left: 12%; font-size: 14px;\">Wine (originally an acronym for \"Wine Is Not an Emulator\") is a compatibility layer capable of running Windows applications on several POSIX-compliant operating systems, such as Linux, Mac OSX, & BSD. Instead of simulating internal Windows logic like a virtual machine or emulator, Wine translates Windows API calls into POSIX calls on-the-fly, eliminating the performance and memory penalties of other methods and allowing you to cleanly integrate Windows applications into your desktop.</p>\r\n</div>\r\n[/one_half]\r\n[one_half last]\r\n<img src=\"http://domino4wine.com/wp-content/uploads/2014/10/domino_Administrator.png\" alt=\"domino_Administrator\" width=\"610\" height=\"437\" class=\"aligncenter size-full wp-image-412\" />\r\n[/one_half]\r\n[clear]\r\n[/raw]\r\n[/section]\r\n\r\n[section bgcolor=\"#fff\" padding=\"0\"]\r\n[raw]\r\n[one_half]\r\n<h3 style=\"text-align: right; top: 50%; color: #6B0031; padding-top: 160px;\">Domino Designer is now running<br/> on Linux and Mac OsX with WINE</h3>\r\n[/one_half]\r\n[one_half last]\r\n<img src=\"http://domino4wine.com/wp-content/uploads/2014/10/domino_Designer.png\" alt=\"domino_Designer\" width=\"610\" height=\"437\" class=\"aligncenter size-full wp-image-413\" />\r\n[/one_half]\r\n[clear]\r\n[/raw]\r\n[/section]\r\n\r\n[section bgcolor=\"#f9f9f9\" padding=\"0\"]\r\n[raw]\r\n[one_half]\r\n<h3 style=\"text-align: right; top: 50%; color: #6B0031; padding-top: 160px;\">Lotus Notes on Linux and Mac OsX </h3>\r\n[/one_half]\r\n[one_half last]\r\n<img src=\"http://domino4wine.com/wp-content/uploads/2014/10/domino_Notes.png\" alt=\"domino_Notes\" width=\"610\" height=\"437\" class=\"aligncenter size-full wp-image-415\" />\r\n[/one_half]\r\n[clear]\r\n[/raw]\r\n[/section]', 'Domino4Wine', '', 'inherit', 'closed', 'open', '', '14-revision-v1', '', '', '2015-01-09 12:20:54', '2015-01-09 12:20:54', '', '14', 'http://dev.domino4wine.com/14-revision-v1/', '0', 'revision', '', '0');
