INSERT INTO `wp_afp_config_names` VALUES('99', '18', 'submit_limit[notification]', '{\"type\":\"textarea\",\"attributes\":{\"cols\":30,\"rows\":\"3\"}}', 'Notification Message', 'The maximum number of submits has been reached.', 'This is the notification the visitors will get if the number of form submits is reached.', '2');
INSERT INTO `wp_afp_config_names` VALUES('100', '18', 'submit_limit[hide_form]', '{\"type\":\"select\",\"options\":{\"1\":\"Yes\",\"0\":\"No\"}}', 'Hide Form if number of submits has been reached?', '1', 'In case you select \'Yes\': if the number of submits has been reached the form will not show when you load the page', '3');
INSERT INTO `wp_afp_config_names` VALUES('101', '18', 'submit_limit[show_js_alert]', '{\"type\":\"select\",\"options\":{\"1\":\"Yes\",\"0\":\"No\"}}', 'Alert Visitor while the form is being filled?', '1', 'In case you select \'Yes\': if the number of submits has been reached a message will pop up letting the visitor know that no further submits are allowed', '4');
INSERT INTO `wp_afp_config_names` VALUES('102', '19', 'availability[start]', '{\"type\":\"text\",\"attributes\":{\"size\":27,\"id\":\"AvailabilityStartDate\"}}', 'Start Date', '', 'From which date you want to have this form active? (leave empty if there is no start date)', '1');
INSERT INTO `wp_afp_config_names` VALUES('103', '19', 'availability[end]', '{\"type\":\"text\",\"attributes\":{\"size\":27,\"id\":\"AvailabilityEndDate\"}}', 'End Date', '', 'Until which date you want to have this form active? (leave empty if there is no end date)', '2');
INSERT INTO `wp_afp_config_names` VALUES('104', '19', 'availability[not_active_yet_message]', '{\"type\":\"textarea\",\"attributes\":{\"cols\":30,\"rows\":\"3\"}}', 'Message to show if the Start Date hasn\'t arrived * <em>optional</em>', '', 'Message the visitors will see in case the form schedule date has not yet started.', '3');
INSERT INTO `wp_afp_config_names` VALUES('105', '19', 'availability[already_expired_message]', '{\"type\":\"textarea\",\"attributes\":{\"cols\":30,\"rows\":\"3\"}}', 'Message to show if the End Date has passed * <em>optional</em>', '', 'Message the visitors will see in case the end date already passed and the form is not active anymore.', '4');
INSERT INTO `wp_afp_config_names` VALUES('106', '20', 'promote[enabled]', '{\"type\":\"select\",\"options\":{\"1\":\"True\",\"0\":\"False\"}}', 'Enable?', '1', 'Make some extra cash by promoting AJAX Form Pro to your visitors.', '1');
