INSERT INTO `wp_afp_config_names` VALUES('117', '11', 'notifications[success_output_from_template]', '', 'Load \"Success Output HTML Code\" from Template?', '', 'Would you like a custom output message? Select the desired template. To understand how to create templates, <a target=\"_blank\" href=\"http://www.ajaxformpro.com/wiki/?p=707\">click here to view the documentation about it</a>.<br /><br />If no template is loaded \"Success Output HTML Code\" will show as default.', '8');
INSERT INTO `wp_afp_config_names` VALUES('1000', '1', 'hook', '{\"type\":\"radio\",\"options\":{\"1\":\"Yes\",\"0\":\"No\"}}', 'Load in WordPress custom pages?', '0', 'This is useful when you want to show a form OUTSIDE posts and pages on your WordPress blog! Make sure to set it to YES if you want a form to load in common locations such as: siderbar, footer of the website etc.', '0');
INSERT INTO `wp_afp_config_names` VALUES('1001', '1', 'in_lightbox', '{\"type\":\"radio\",\"options\":{\"1\":\"Yes\",\"0\":\"No\"}}', 'Load inside Lightbox when triggered by click?', '0', 'Would you like to have a lightbox with the form that popups when you click on a link/button? Enable it! Note that \"Load in WordPress custom pages?\" must also be enabled for this feature to work!', '0');
